/* faa/cav/004/FAACAV004ER2select */ SELECT SUBSTR(PROF_YM,1,4) || '.' || SUBSTR(PROF_YM,5,2) AS 증명년월 AS PROF_YM /* 증명연월 */,
    SUBSTR(BLL_FRST_RCPT_YMD,1,4) || '.' || SUBSTR(BLL_FRST_RCPT_YMD,5,2)|| '.' || SUBSTR(BLL_FRST_RCPT_YMD,7,2) AS 최초접수일 AS BLL_FRST_RCPT_YMD /* 계산서최초접수일자 */,
    SUBSTR(LAST_RCPT_YMD,1,4) || '.' || SUBSTR(LAST_RCPT_YMD,5,2)|| '.' || SUBSTR(LAST_RCPT_YMD,7,2) AS 최종접수일 AS LAST_RCPT_YMD /* 최종접수일자 */,
    BLL_RCPT_RUD AS 정정횟수 AS BLL_RCPT_RUD /* 계산서접수회차 */
FROM TWFAACAV004L WHERE 1=1 AND JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM BETWEEN #strYr#||'00' AND #strYr#||'13' ORDER BY PROF_YM