/* faa/cav/010/FAACAV010ESelectMainPop_23 */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    RFAMT_DCSN_AMT_NO /* 환급금결정금액번호 */,
    TSMM_TRAC_NTFCTN_AMT /* 당월이체통보금액 */,
    TSMM_TRAC_NTFCTN_ACTOT_AMT /* 당월이체통보누계금액 */,
    TSMM_GIVE_DCSN_AMT /* 당월지급결정금액 */,
    TSMM_GIVE_DCSN_ACTOT_AMT /* 당월지급결정누계금액 */,
    TSMM_GIVE_AMT /* 당월지급금액 */,
    TSMM_GIVE_ACTOT_AMT /* 당월지급누계금액 */,
    TRAC_NTFCTN_OTSPY_AMT /* 이체통보미지급금액 */,
    GIVE_DCSN_OTSPY_AMT /* 지급결정미지급금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
