/* faa/cav/010/FAACAV010ESelectMainPop_24A */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    SSEC_CD||'-'||DITM_CD||'-'||SITM_CD AS SSEC_CD /* 관코드 */,
    TQR_INCM_EXPND_AMT /* 본분기수입지출금액 */,
    TQR_INCM_EXPND_ACTOT_AMT /* 본분기수입지출누계금액 */,
    TQR_ANRVN_PAY_AMT /* 본분기세입납부금액 */,
    TQR_ANRVN_PAY_ACTOT_AMT /* 본분기세입납부누계금액 */,
    INCM_SBTR_ACTOT_AMT /* 수입차감누계금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
FROM TWFAACAV062L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND ACNTG_YR = SUBSTR(#PROF_YM#,0,4)) M ) AS ACNB_CD FROM TWFAACAV034L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM = #PROF_YM# AND ((SUM_SE_ID != '49' AND AUEX_SECD != '2' ) OR (SUM_SE_ID = '49' AND AUEX_SECD = '1')) ORDER BY SUM_SE_ID DESC, AUEX_SECD, CHIF_CD, SSEC_CD, DITM_CD, SITM_CD