/* faa/cav/010/FAACAV010ESelectMainPop_25 */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    SSEC_CD||'-'||ITM_CD||'-'||SITM_CD AS SSEC_CD /* 관코드 */,
    TQR_CLCTN_DCSN_AMT /* 본분기징수결정금액 */,
    TQR_CLCTN_DCSN_ACTOT_AMT /* 본분기징수결정누계금액 */,
    TQR_RCVMT_AMT /* 본분기수납금액 */,
    TQR_RCVMT_ACTOT_AMT /* 본분기수납누계금액 */,
    TQR_RMBR_AMT /* 본분기환급금액 */,
    TQR_RMBR_ACTOT_AMT /* 본분기환급누계금액 */,
    TQR_LSNPY_AMT /* 본분기불납결손금액 */,
    TQR_LSNPY_ACTOT_AMT /* 본분기불납결손누계금액 */,
    UNPAID_AMT /* 미납금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
FROM TWFAACAV062L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND ACNTG_YR = SUBSTR(#PROF_YM#,0,4)) M ) AS ACNB_CD ,F_AV_ACCMN_NM(JRSD_CD,ACNTG_CD,BLL_KDCD,PROF_RBPRSN_SQNO,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS PROF_RBPRSN_INST_NM ,JRSD_CD ,ACNTG_CD ,BLL_KDCD ,PROF_RBPRSN_SQNO ,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO ,PROF_YM ,TPOF_CYCL ,SSEC_CD,ITM_CD,SITM_CD ,SUM_SE_ID FROM TWFAACAV054L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM = #PROF_YM# ORDER BY SUM_SE_ID DESC,SSEC_CD, ITM_CD, SITM_CD