    CASE WHEN BLL_KDCD ='28' AND SUM_SE_ID ='49' THEN '합계' ELSE BLL_SBJCT_NM END AS BLL_SBJCT_NM /* 계산서과목명 */,
    LTYR_CRFW_AMT /* 전년이월금액 */,
    THYR_RCVMT_AMT /* 본년도수납금액 */,
    THYR_DCRS_RELS_AMT /* 본년도감소불출금액 */,
    THYR_DCRS_ANRVN_PAY_AMT /* 본년도감소세입납부금액 */,
    THYR_DCRS_AMT /* 본년도감소금액 */,
    CYE_NOW_CASH_AMT /* 본년도말현재현금금액 */,
    CYE_NOW_BOK_DPSTG_AMT /* 본년도말현재한국은행예탁금액 */,
    CYE_NOW_ACTOT_AMT /* 본년도말현재누계금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
