/* faa/cav/010/FAACAV010ESelectMainPop_30 */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    CASE WHEN SUM_SE_ID IN ('51','49') THEN '' ELSE CMDTY_IDNO END AS CMDTY_IDNO /* 물품식별번호 */,
    CASE WHEN SUM_SE_ID IN ('51','49') THEN '' ELSE CMDTY_CD END AS CMDTY_CD /* 물품코드 */,
    CASE WHEN SUM_SE_ID IN ('51','49') THEN '' ELSE CMDTY_UNIT_NM END AS CMDTY_UNIT_NM /* 물품단위명 */,
    PYE_NOW_QTY /* 전년도말현재수량 */,
    PYE_NOW_PRC /* 전년도말현재가격 */,
    THYR_INCRS_QTY /* 본년도증가수량 */,
    THYR_AJMT_PRC /* 본년도조정가격 */,
    THYR_DCRS_QTY /* 본년도감소수량 */,
    THYR_DCRS_PRC /* 본년도감소가격 */,
    THYR_CMPR_ICDC_QTY /* 본년도비교증감수량 */,
    THYR_CMPR_ICDC_PRC /* 본년도비교증감가격 */,
    CYE_NOW_QTY_AMT /* 본년도말현재수량금액 */,
    CYE_NOW_AMT_PRC /* 본년도말현재금액가격 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
FROM TWFAACAV062L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND ACNTG_YR = SUBSTR(#PROF_YM#,0,4)) M ) AS ACNB_CD ,F_AV_ACCMN_NM(JRSD_CD,ACNTG_CD,BLL_KDCD,PROF_RBPRSN_SQNO,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS PROF_RBPRSN_INST_NM ,SUM_SE_ID ,JRSD_CD ,ACNTG_CD ,BLL_KDCD ,PROF_RBPRSN_SQNO ,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO ,PROF_YM ,CMDTY_IDNO AS GDS_SRNO_1 ,CMDTY_CD AS GDS_CD_1 ,CMDTY_UNIT_NM AS GDS_UNT_NM_1 FROM TWFAACAV031L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM = #PROF_YM# ORDER BY (CASE WHEN SUM_SE_ID = '51' THEN 1 ELSE CASE WHEN SUM_SE_ID = '49' THEN 2 ELSE 3 END END ), SUM_SE_ID , CMDTY_IDNO, CMDTY_CD