/* faa/cav/010/FAACAV010ESelectMainPop_39 */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    JRSD_CD /* 소관코드 */,
    ACNTG_CD /* 회계코드 */,
    BLL_KDCD /* 계산서종류코드 */,
    PROF_RBPRSN_SQNO /* 증명책임자순번 */,
    GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO /* 관서운영경비출납공무원순번 */,
    PROF_YM /* 증명연월 */,
    ACNTG_CD||'-'||CHIF_CD||'-'||SSEC_CD||'-'||DITM_CD||'-'||SITM_CD AS ACNTG_CD /* 계정코드 */,
    CASE WHEN length(ABS(TQR_EXPND_AMT)) > 3 THEN TO_CHAR(substr(TQR_EXPND_AMT, 0, length(TQR_EXPND_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_EXPND_AMT,length(TQR_EXPND_AMT) -2) ELSE TO_CHAR(TQR_EXPND_AMT) END AS TQR_EXPND_AMT /* 본분기지출금액 */,
    CASE WHEN length(ABS(PRVTM_EXPND_AMT)) > 3 THEN TO_CHAR(substr(PRVTM_EXPND_AMT, 0, length(PRVTM_EXPND_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(PRVTM_EXPND_AMT,length(PRVTM_EXPND_AMT) -2) ELSE TO_CHAR(PRVTM_EXPND_AMT) END AS PRVTM_EXPND_AMT /* 전기지출금액 */,
    CASE WHEN length(ABS(TQR_RTN_AMT)) > 3 THEN TO_CHAR(substr(TQR_RTN_AMT, 0, length(TQR_RTN_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_RTN_AMT,length(TQR_RTN_AMT) -2) ELSE TO_CHAR(TQR_RTN_AMT) END AS TQR_RTN_AMT /* 본분기반납금액 */,
    CASE WHEN length(ABS(TQR_SBJCT_RCTF_AMT)) > 3 THEN TO_CHAR(substr(TQR_SBJCT_RCTF_AMT, 0, length(TQR_SBJCT_RCTF_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_SBJCT_RCTF_AMT,length(TQR_SBJCT_RCTF_AMT) -2) ELSE TO_CHAR(TQR_SBJCT_RCTF_AMT) END AS TQR_SBJCT_RCTF_AMT /* 본분기과목경정금액 */,
    CASE WHEN length(ABS(FUND_EXPND_SBTR_AMT)) > 3 THEN TO_CHAR(substr(FUND_EXPND_SBTR_AMT, 0, length(FUND_EXPND_SBTR_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(FUND_EXPND_SBTR_AMT,length(FUND_EXPND_SBTR_AMT) -2) ELSE TO_CHAR(FUND_EXPND_SBTR_AMT) END AS FUND_EXPND_SBTR_AMT /* 기금지출차감금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
FROM TWFAACAV062L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND ACNTG_YR = SUBSTR(#PROF_YM#,0,4)) M ) AS ACNB_CD ,F_AV_ACCMN_NM(JRSD_CD,ACNTG_CD,BLL_KDCD,PROF_RBPRSN_SQNO,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS PROF_RBPRSN_INST_NM ,SUM_SE_ID ,ACNT_CD, CHIF_CD,SSEC_CD,DITM_CD,SITM_CD FROM TWFAACAV027L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM = #PROF_YM# ORDER BY SUM_SE_ID DESC, ACNT_CD, SSEC_CD, DITM_CD, SITM_CD