/* faa/cav/010/FAACAV010ESelectMainPop_41 */ SELECT CASE WHEN SUM_SE_ID='51' THEN '월계' ELSE CASE WHEN SUM_SE_ID='49' THEN '합계' ELSE SUM_SE_ID END END AS SUM_SE_ID /* 합계구분아이디 */,
    CHIF_CD /* 장코드 */,
    SSEC_CD /* 관코드 */,
    ITM_CD /* 항코드 */,
    SITM_CD /* 목코드 */,
    CASE WHEN length(ABS(TQR_RCT_AMT)) > 3 THEN TO_CHAR(substr(TQR_RCT_AMT, 0, length(TQR_RCT_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_RCT_AMT,length(TQR_RCT_AMT) -2) ELSE TO_CHAR(TQR_RCT_AMT) END AS TQR_RCT_AMT /* 본분기영수금액 */,
    CASE WHEN length(ABS(TQR_RCT_ACTOT_AMT)) > 3 THEN TO_CHAR(substr(TQR_RCT_ACTOT_AMT, 0, length(TQR_RCT_ACTOT_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_RCT_ACTOT_AMT,length(TQR_RCT_ACTOT_AMT) -2) ELSE TO_CHAR(TQR_RCT_ACTOT_AMT) END AS TQR_RCT_ACTOT_AMT /* 본분기영수누계금액 */,
    CASE WHEN length(ABS(TQR_PAY_AMT)) > 3 THEN TO_CHAR(substr(TQR_PAY_AMT, 0, length(TQR_PAY_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_PAY_AMT,length(TQR_PAY_AMT) -2) ELSE TO_CHAR(TQR_PAY_AMT) END AS TQR_PAY_AMT /* 본분기납부금액 */,
    CASE WHEN length(ABS(TQR_PAY_AMT)) > 3 THEN TO_CHAR(substr(TQR_PAY_AMT, 0, length(TQR_PAY_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(TQR_PAY_AMT,length(TQR_PAY_AMT) -2) ELSE TO_CHAR(TQR_PAY_AMT) END AS TQR_PAY_AMT /* 본분기납부금액 */,
    CASE WHEN length(ABS(UNPAID_AMT)) > 3 THEN TO_CHAR(substr(UNPAID_AMT, 0, length(UNPAID_AMT) -3),'FM999,999,999,999,999') ||'.'||substr(UNPAID_AMT,length(UNPAID_AMT) -2) ELSE TO_CHAR(UNPAID_AMT) END AS UNPAID_AMT /* 미납금액 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS JRSD_NM /* 소관명 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS ACNTG_CDNM /* 회계코드명 */,
    (SELECT AGGR_CONCAT (M.EAN_CD,', ')
FROM (SELECT dguard.decrypt('TB_ENC','EAN',ACTNO_SCRTY) || '-' || ACNT_SBJCT_SECD AS DECRYPT
FROM TWFAACAV062L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND ACNTG_YR = SUBSTR(#PROF_YM#,0,4)) M ) AS ACNB_CD ,F_AV_ACCMN_NM(JRSD_CD,ACNTG_CD,BLL_KDCD,PROF_RBPRSN_SQNO,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO) AS PROF_RBPRSN_INST_NM ,SUM_SE_ID ,JRSD_CD ,ACNTG_CD ,BLL_KDCD ,PROF_RBPRSN_SQNO ,GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO ,PROF_YM ,RA_RCDB_NO FROM TWFAACAV032L WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM = #PROF_YM# ORDER BY SUM_SE_ID DESC,CHIF_CD, SSEC_CD, ITM_CD, SITM_CD