/* faa/cav/017/FAACAV017EMainDtlDisagselect */ /*기관별불부합현황 조회*/ SELECT MN.JRSD_CD||'-'||MN.ACNTG_CD||'-'||MN.BLL_KDCD||'-'||MN.PROF_RBPRSN_SQNO||'-'|| MN.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS JRSD_CD /* 소관코드 */,
    MN.PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    MN.JRSD_CD /* 소관코드 */,
    MN.ACNTG_CD /* 회계코드 */,
    MN.BLL_KDCD /* 계산서종류코드 */,
    MN.PROF_RBPRSN_SQNO /* 증명책임자순번 */,
    MN.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO /* 관서운영경비출납공무원순번 */,
    CD1.ACNTG_CDNM /* 회계코드명 */,
    DTL.PROF_YM /* 증명연월 */,
    DTL.DISAG_NO /* 불부합번호 */,
    CD.RMRK_DTL_CN /* 비고상세내용 */,
    CON.MDFCN_DT /* 수정일시 */
FROM TWFAACAV020D dtl, TWFAACAV024C cd, TWFAACAV003M mn, TWFAACAV024C cd1, TWFAACAV019M con WHERE MN.JRSD_CD = DTL.JRSD_CD AND MN.ACNTG_CD = DTL.ACNTG_CD AND MN.BLL_KDCD = DTL.BLL_KDCD AND MN.PROF_RBPRSN_SQNO = DTL.PROF_RBPRSN_SQNO AND MN.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = DTL.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND MN.JRSD_CD = CON.JRSD_CD AND MN.ACNTG_CD = CON.ACNTG_CD AND MN.BLL_KDCD = CON.BLL_KDCD AND MN.PROF_RBPRSN_SQNO = CON.PROF_RBPRSN_SQNO AND MN.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = CON.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND DTL.DISAG_CD = CD.ACNTG_CD AND CD.ACNTG_SECD = '12' AND MN.BLL_KDCD = CD1.ACNTG_CD AND CD1.ACNTG_SECD = '03' AND CON.PROF_YM = DTL.PROF_YM AND CON.PROF_YM < #strYm# AND MN.PROF_RBPRSN_INST_CD = #INST_CD# ORDER BY MN.JRSD_CD, MN.ACNTG_CD, MN.BLL_KDCD, MN.PROF_RBPRSN_SQNO, MN.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO, DTL.PROF_YM