/* faa/cav/018/FAACAV018EselectReport1 */ /*불부합종결목록*/ SELECT DIS.PROF_RBPRSN_RB_SECD||'0'||'-'||DIS.CPD_CD||'0' AS 담당국과 AS PROF_RBPRSN_RB_SECD /* 증명책임자소관국구분코드 */,
    DISAG_NO AS 불부합번호 AS DISAG_NO /* 불부합번호 */,
    substring(PROF_YM,1,4)||'.'||substring(PROF_YM,5,2) AS 증명년월 AS PROF_YM /* 증명연월 */,
    DIS.JRSD_CD||'-'||DIS.ACNTG_CD||'-'||DIS.BLL_KDCD||'-'||DIS.PROF_RBPRSN_SQNO||'-'|| DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    CASE WHEN (DIS.BLL_KDCD='17' AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (DIS.BLL_KDCD='18' AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' ELSE CASE WHEN (DIS.BLL_KDCD='22' AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') or (DIS.BLL_KDCD='23' AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') or (DIS.BLL_KDCD='24' AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비출납계산서' ELSE CD.ACNTG_CDNM END END AS 계산서명 AS BLL_KDCD /* 계산서종류코드 */,
    substring(DISAG_IDNTY_DT,1,4)||'.'||substring(DISAG_IDNTY_DT,5,2)||'.'||substring(DISAG_IDNTY_DT,7,2) AS 불부합등록일 AS DISAG_IDNTY_DT /* 불부합확인일시 */,
    substring(MDFCN_DT,1,4)||'.'||substring(MDFCN_DT,5,2)||'.'||substring(MDFCN_DT,7,2) AS 종결일 AS MDFCN_DT /* 수정일시 */
FROM TWFAACAV019M DIS, TWFAACAV024C CD, TWFAACAV024C CD1, TBDCMACM015M CM , TWFAACAV003M MN WHERE 1 = 1 AND PROF_YM BETWEEN #strYr#||'00' AND #strYr#||'13' AND PROF_YM = #PROF_YM# AND DIS.JRSD_CD = #JRSD_CD# AND DIS.ACNTG_CD = #ACNTG_CD# AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('17','18') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.BLL_KDCD IN('27') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('27') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('27') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.BLL_KDCD IN('19') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('19') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('19') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.BLL_KDCD IN('22','23','24') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('22','23','24') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('22','23','24') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.BLL_KDCD IN('42','43') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND DIS.BLL_KDCD IN('42','43') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO &lt;&gt; '000' AND DIS.BLL_KDCD IN('42','43') AND DIS.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND DIS.PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND DIS.MDFCN_DT IS NOT NULL AND DIS.MDFCN_DT IS NULL AND DIS.PROF_RBPRSN_RB_SECD||'0' = #strBrCd# 