/* faa/cav/024/FAACAV024ESelectProfYmAblt */ SELECT NVL(PROF_YM,0) AS PROF_YM /* 증명연월 */
FROM TWFAACAV002H WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND PROF_YM > (CASE WHEN #pType# = '01' THEN CASE WHEN SUBSTR(#strAbltDt1#||#strAbltDt2#,5,2)='12' THEN SUBSTR(#strAbltDt1#||#strAbltDt2#,1,4)||'13' ELSE #strAbltDt1#||#strAbltDt2# END WHEN #pType# = '03' THEN CASE WHEN SUBSTR(#strAbltDt1#||#strAbltDt2#,5,2) > '09' THEN SUBSTR(#strAbltDt1#||#strAbltDt2#,1,4)||'04' WHEN SUBSTR(#strAbltDt1#||#strAbltDt2#,5,2) > '06' THEN SUBSTR(#strAbltDt1#||#strAbltDt2#,1,4)||'03' WHEN SUBSTR(#strAbltDt1#||#strAbltDt2#,5,2) > '03' THEN SUBSTR(#strAbltDt1#||#strAbltDt2#,1,4)||'02' ELSE SUBSTR(#strAbltDt1#||#strAbltDt2#,1,4)||'01' END WHEN #pType# = '05' THEN #strAbltDt1#||'13' END) AND BLL_RCPTN_STTS_SECD='A'