/* faa/cav/024/FAACAV024ESelectReport2 */ SELECT JRSD_CD||'-'||A.ACNTG_CD||'-'||A.BLL_KDCD||'-'||PROF_RBPRSN_SQNO||'-'||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS ACNTG_CD /* 회계코드 */,
