    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    F_AV_ACNT_CD_NM(A.ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    CASE WHEN (A.BLL_KDCD ='17' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' WHEN (A.BLL_KDCD='22' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비' ELSE ACNTG_CDNM END 계산서명 AS BLL_KDCD /* 계산서종류코드 */,
