/* faa/cav/024/FAACAV024ESelectReport3_2 */ SELECT PROF_RBPRSN_INST_NM AS 증명기관명 AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    CASE WHEN (A.BLL_KDCD ='17' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' WHEN (A.BLL_KDCD='22' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비' ELSE ACNTG_CDNM END 계산증명서종류 AS BLL_KDCD /* 계산서종류코드 */,
    JBPS_NM AS 직위명 AS JBPS_NM /* 직위명 */,
    CASE WHEN SUBSTR(PROF_RBPRSN_CRT_YMD,1,4) = '0000' THEN '' ELSE SUBSTR(PROF_RBPRSN_CRT_YMD,1,4) || '.' || SUBSTR(PROF_RBPRSN_CRT_YMD,5,2) || '.' || SUBSTR(PROF_RBPRSN_CRT_YMD,7,2) END AS 신설년월 AS PROF_RBPRSN_CRT_YMD /* 증명책임자생성일자 */,
    CASE WHEN SUBSTR(PROF_RBPRSN_ABL_YMD,1,4) = '9999' THEN '' ELSE SUBSTR(PROF_RBPRSN_ABL_YMD,1,4) || '.' || SUBSTR(PROF_RBPRSN_ABL_YMD,5,2) || '.' || SUBSTR(PROF_RBPRSN_ABL_YMD,7,2) END AS 폐지년월 AS PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */,
    F_DPT_INFO (RPAD(RB_CD,3,'0')||RPAD(PROF_RBPRSN_CPD_SECD,3,'0'), '1') AS 국과 AS RB_CD /* 소관국코드 */,
    JRSD_CD||'-'||A.ACNTG_CD||'-'||A.BLL_KDCD||'-'||PROF_RBPRSN_SQNO||'-'||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명자부호 AS ACNTG_CD /* 회계코드 */
FROM TWFAACAV003M A, TWFAACAV024C B, TBDCMACM002M C WHERE A.BLL_KDCD = B.ACNTG_CD AND RPAD(A.PROF_RBPRSN_RB_SECD,3,'0')||RPAD(PROF_RBPRSN_CPD_SECD,3,'0') = C.SRECS_DEPT_CD(+) AND ACNTG_SECD = '03' AND JRSD_CD = #JRSD_CD# AND A.ACNTG_CD = #ACNTG_CD#