/* faa/cav/024/FAACAV024ESelectReport4 */ SELECT DISTINCT A.PROF_RBPRSN_INST_CD AS INST_CD /* 기관코드 */,
    A.PROF_RBPRSN_INST_NM AS 기관명 AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    B.ACNTG_CD AS ACNTG_CD /* 회계코드 */,
    B.ACNTG_CDNM AS 소관명 AS ACNTG_CDNM /* 회계코드명 */
FROM TWFAACAV003M A, TWFAACAV024C B WHERE ((A.PROF_RBPRSN_CRT_YMD >= #PROF_RBPRSN_CRT_YMD#||#strSM#||'01' AND A.PROF_RBPRSN_CRT_YMD = #PROF_RBPRSN_CRT_YMD#||#strSM#||'31' AND A.PROF_RBPRSN_ABL_YMD = #PROF_RBPRSN_ABL_YMD#||#strEM#||'31')) AND SUBSTRING(A.PROF_RBPRSN_INST_CD,1,3) = B.ACNTG_CD AND B.ACNTG_SECD = '02' ORDER BY INST_CD