/* faa/cav/024/FAACAV024ESelectReport7 */ SELECT PROF_RBPRSN_INST_NM AS 기관명 AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    JRSD_CD||'-'||A.ACNTG_CD||'-'||A.BLL_KDCD||'-'||PROF_RBPRSN_SQNO||'-'||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS ACNTG_CD /* 회계코드 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    F_AV_ACNT_CD_NM(A.ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    CASE WHEN (A.BLL_KDCD ='17' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' WHEN (A.BLL_KDCD='22' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비' ELSE ACNTG_CDNM END AS 계산서명 AS BLL_KDCD /* 계산서종류코드 */,
    JBPS_NM AS 회계직명 AS JBPS_NM /* 직위명 */,
    SUBSTR(PROF_RBPRSN_CRT_YMD,1,4)||'.'||SUBSTR(PROF_RBPRSN_CRT_YMD,5,2)||'.'||SUBSTR(PROF_RBPRSN_CRT_YMD,7,2) AS 신설일 AS PROF_RBPRSN_CRT_YMD /* 증명책임자생성일자 */,
    SUBSTR(PROF_RBPRSN_ABL_YMD,1,4)||'.'||SUBSTR(PROF_RBPRSN_ABL_YMD,5,2)||'.'||SUBSTR(PROF_RBPRSN_ABL_YMD,7,2) AS 폐지일 AS PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */,
    SUBSTR(INST_CD,1,3)||'-'||SUBSTR(INST_CD,4,3)||'-'||SUBSTR(INST_CD,7,3)||'-'||SUBSTR(INST_CD,10,3) AS 기관부호 AS INST_CD /* 기관코드 */,
    F_DPT_INFO (RPAD(RB_CD,3,'0')||RPAD(PROF_RBPRSN_CPD_SECD,3,'0'), '1') AS 담당국과 AS RB_CD /* 소관국코드 */,
    INLDR_NM AS 기관장명 AS INLDR_NM /* 기관장명 */,
    PROF_RBPRSN_BRNO AS 사업자등록번호 AS PROF_RBPRSN_BRNO /* 증명책임자사업자등록번호 */,
    DOC_ENFC_NO AS 문서번호 AS DOC_ENFC_NO /* 문서시행번호 */,
    SUBSTR(DOC_ENFC_YMD,1,4)||'.'||SUBSTR(DOC_ENFC_YMD,5,2)||'.'||SUBSTR(DOC_ENFC_YMD,7,2) AS 문서시행일 AS DOC_ENFC_YMD /* 문서시행일자 */,
    EXPND_OFER_NM AS 담당공무원명 AS EXPND_OFER_NM /* 지출담당관명 */,
    PROF_RBPRSN_EXPND_OFER_RRNO_SCRTY AS 주민번호1 AS PROF_RBPRSN_EXPND_OFER_RRNO_SCRTY /* 증명책임자지출담당관주민등록번호보안 */,
    PROF_RBPRSN_MDFR_NM AS 작성자명 AS PROF_RBPRSN_MDFR_NM /* 증명책임자수정자명 */,
    PROF_RBPRSN_MDFR_RRNO_SCRTY AS 주민번호2 AS PROF_RBPRSN_MDFR_RRNO_SCRTY /* 증명책임자수정자주민등록번호보안 */,
    A.PROF_RBPRSN_TELNO AS 전화번호 AS PROF_RBPRSN_TELNO /* 증명책임자전화번호 */,
    SUBSTR(A.PROF_RBPRSN_ZIP,1,3)||'-'||SUBSTR(A.PROF_RBPRSN_ZIP,4,3) AS 우편번호 AS PROF_RBPRSN_ZIP /* 증명책임자우편번호 */,
    A.PROF_RBPRSN_ADDR AS 주소 AS PROF_RBPRSN_ADDR /* 증명책임자주소 */
FROM TWFAACAV003M A, TWFAACAV024C B, TBDCMACM002M C WHERE A.BLL_KDCD = B.ACNTG_CD AND RPAD(A.PROF_RBPRSN_RB_SECD,3,'0')||RPAD(PROF_RBPRSN_CPD_SECD,3,'0') = C.SRECS_DEPT_CD(+) AND ACNTG_SECD = '03' AND A.JRSD_CD = #JRSD_CD# AND A.ACNTG_CD = #ACNTG_CD# AND A.BLL_KDCD = #BLL_KDCD# AND A.PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO#