/* faa/cav/026/FAACAV025EMainSelectForCsvLink */ /* 증명책임자부호,소관명,계산서명,회계코드,기관명,기관책임자,지정년월,폐지년월,작업일자,소관코드*/ SELECT chr(34)|| #linkId# ||chr(34) AS CHR,
    chr(34)|| A.JRSD_CD || A.ACNTG_CD || A.BLL_KDCD || A.PROF_RBPRSN_SQNO || A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO ||chr(34) AS CHR,
    /*증명책임자부호*/ chr(34)|| A.JRSD_CD ||chr(34) AS CHR,
    /*소관코드*/ chr(34)|| F_AV_BLL_COPT_CD_NM (A.JRSD_CD) ||chr(34) AS CHR,
    /*소관명*/ CASE WHEN (A.BLL_KDCD ='17' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '"관서운영경비출납계산서"' WHEN (A.BLL_KDCD='22' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '"기금관서운영경비출납계산서"' ELSE chr(34)|| B.ACNTG_CDNM ||chr(34) END AS BLL_KDCD /* 계산서종류코드 */,
    /*계산서명*/ chr(34)|| A.ACNTG_CD ||chr(34) AS CHR,
    /*회계코드*/ chr(34)|| F_AV_ACNT_CD_NM (A.ACNTG_CD) ||chr(34) AS CHR,
    /*회계코드명*/ chr(34)|| PROF_RBPRSN_INST_NM ||chr(34) AS CHR,
    /*기관명*/ chr(34)|| JBPS_NM || chr(34) AS CHR,
    /*증명책임자 직위*/ CASE WHEN A.PROF_RBPRSN_CRT_YMD = '000000' THEN ' ' ELSE chr(34)|| A.PROF_RBPRSN_CRT_YMD ||chr(34) END AS PROF_RBPRSN_CRT_YMD /* 증명책임자생성일자 */,
    /*지정년월*/ CASE WHEN A.PROF_RBPRSN_ABL_YMD = '999999' THEN ' ' ELSE chr(34)|| A.PROF_RBPRSN_ABL_YMD ||chr(34) END KK /*폐지년월*/ /* '' AS LL 연계메시지ID*/ /* '' AS MM 연계처리상세일시*/ AS PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */
FROM TWFAACAV003M A, TWFAACAV024C B, TBDCMACM002M C WHERE A.BLL_KDCD = B.ACNTG_CD AND RPAD(A.PROF_RBPRSN_RB_SECD,3,'0') || RPAD(PROF_RBPRSN_CPD_SECD,3,'0') = C.SRECS_DEPT_CD (+ ) AND B.ACNTG_SECD = '03' AND A.JRSD_CD NOT IN ('301','069','155') AND A.BLL_KDCD NOT IN ('41', '42', '43') AND A.PROF_RBPRSN_MDFCN_YMD >= #strStartDt# AND A.PROF_RBPRSN_MDFCN_YMD AND SUBSTR(A.PROF_RBPRSN_ABL_YMD, 1, 4) >= #strABltDt# AND A.JRSD_CD|| '-' ||A.ACNTG_CD|| '-' || A.BLL_KDCD || '-' || A.PROF_RBPRSN_SQNO || '-' || A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO NOT IN #strAA[]# AND A.JRSD_CD|| '-' ||A.ACNTG_CD|| '-' || A.BLL_KDCD || '-' || A.PROF_RBPRSN_SQNO || '-' || A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO NOT IN #strBB# ORDER BY BB