/* faa/cav/026/FAACAV026EReportSelect */ SELECT *
FROM ( SELECT T.*,
    COUNT(1) OVER ( ) AS 총계
FROM ( SELECT A.JRSD_CD || '-' || A.ACNTG_CD || '-' || A.BLL_KDCD || '-' || A.PROF_RBPRSN_SQNO || '-' || A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    F_AV_BLL_COPT_CD_NM (A.JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    F_AV_ACNT_CD_NM (A.ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    CASE WHEN (A.BLL_KDCD ='17' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' WHEN (A.BLL_KDCD='22' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비' ELSE B.ACNTG_CDNM END 계산서명 AS BLL_KDCD /* 계산서종류코드 */,
    NVL(C.CNSTN_MBCMT_DEPT_NM,' ') AS 담당국과 AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    PROF_RBPRSN_INST_NM AS 기관명 AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TWFAACAV003M A, TWFAACAV024C B, TBDCMACM002M C WHERE A.BLL_KDCD = B.ACNTG_CD AND RPAD(A.RB_CD,3,'0') || RPAD(PROF_RBPRSN_CPD_SECD,3,'0') = C.SRECS_DEPT_CD (+ ) AND B.ACNTG_SECD = '03' AND A.PROF_RBPRSN_INST_NM = #PROF_RBPRSN_INST_NM# AND A.INST_CD = #INST_CD#||#strOrgCd2#||#strOrgCd3#||#strOrgCd4# AND A.INST_CD BETWEEN #INST_CD#||#strOrgCd2#||#strOrgCd3#||'000' AND #INST_CD#||#strOrgCd2#||#strOrgCd3#||'999' AND A.INST_CD BETWEEN #INST_CD#||#strOrgCd2#||'000000' AND #INST_CD#||#strOrgCd2#||'999999' AND A.INST_CD BETWEEN #INST_CD#||'000000000' AND #INST_CD#||'999999999' ORDER BY 증명책임자부호 ) T )