/* faa/cav/026/FAACAV027EReportSelect */ SELECT A.JRSD_CD || '-' || A.ACNTG_CD || '-' || A.BLL_KDCD || '-' || A.PROF_RBPRSN_SQNO || '-' || A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    F_ORG_INFO(A.PROF_RBPRSN_INST_CD,1) AS 기관명 AS PROF_RBPRSN_INST_CD /* 증명책임자기관코드 */,
    F_AV_BLL_COPT_CD_NM (A.JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    F_AV_ACNT_CD_NM (A.ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    CASE WHEN (A.BLL_KDCD ='17' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='18' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '관서운영경비출납계산서' WHEN (A.BLL_KDCD='22' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='23' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') OR (A.BLL_KDCD='24' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO!= '000') THEN '기금관서운영경비' ELSE B.ACNTG_CDNM END 계산서명 AS BLL_KDCD /* 계산서종류코드 */,
    CASE WHEN A.PROF_RBPRSN_CRT_YMD = '000000' THEN '' ELSE SUBSTR(A.PROF_RBPRSN_CRT_YMD,1,4) || '.' || SUBSTR(A.PROF_RBPRSN_CRT_YMD,5,2) || '.' || SUBSTR(A.PROF_RBPRSN_CRT_YMD,7,2) END AS 신설일자 AS PROF_RBPRSN_CRT_YMD /* 증명책임자생성일자 */,
    CASE WHEN A.PROF_RBPRSN_ABL_YMD = '999999' THEN '' ELSE SUBSTR(A.PROF_RBPRSN_ABL_YMD,1,4) || '.' || SUBSTR(A.PROF_RBPRSN_ABL_YMD,5,2) || '.' || SUBSTR(A.PROF_RBPRSN_ABL_YMD,7,2) END AS 폐지일자 AS PROF_RBPRSN_ABL_YMD /* 증명책임자폐지일자 */
FROM TWFAACAV003M A, TWFAACAV024C B, TBDCMACM002M C WHERE A.BLL_KDCD = B.ACNTG_CD AND RPAD(A.PROF_RBPRSN_RB_SECD,3,'0') || RPAD(PROF_RBPRSN_CPD_SECD,3,'0') = C.SRECS_DEPT_CD (+) AND B.ACNTG_SECD = '03' AND A.BLL_KDCD = #BLL_KDCD# AND A.BLL_KDCD IN ('22','23','24') AND A.BLL_KDCD IN ('17','18') AND A.BLL_KDCD IN ('42','43') AND A.PROF_RBPRSN_CRT_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||#strMm# AND #strYy#||#strMm# AND A.PROF_RBPRSN_ABL_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||#strMm# AND #strYy#||#strMm# AND A.PROF_RBPRSN_CRT_YMD BETWEEN '0000'||'00' AND '9999'||'99' AND A.PROF_RBPRSN_CRT_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'01' AND #strYy#||'03' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'04' AND #strYy#||'06' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'07' AND #strYy#||'09' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'10' AND #strYy#||'12' AND A.PROF_RBPRSN_CRT_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'01' AND #strYy#||'03' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'04' AND #strYy#||'06' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'07' AND #strYy#||'09' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||'10' AND #strYy#||'12' AND A.PROF_RBPRSN_CRT_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_CRT_YMD BETWEEN #strYy#||#strMm# AND #strYy#||#strMm# AND A.PROF_RBPRSN_ABL_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'01' AND #strYy#||'03' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'04' AND #strYy#||'06' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'07' AND #strYy#||'09' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'10' AND #strYy#||'12' AND A.PROF_RBPRSN_ABL_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'01' AND #strYy#||'03' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'04' AND #strYy#||'06' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'07' AND #strYy#||'09' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||'10' AND #strYy#||'12' AND A.PROF_RBPRSN_ABL_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_ABL_YMD BETWEEN #strYy#||#strMm# AND #strYy#||#strMm# AND A.PROF_RBPRSN_CRT_YMD BETWEEN '0000'||'01' AND '9999'||'12' AND A.PROF_RBPRSN_CRT_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_ABL_YMD LIKE '%'|| #strYy# ||'%' AND A.PROF_RBPRSN_CRT_YMD BETWEEN '0000'||'01' AND '9999'||'12' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = '000' AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO != '000' ORDER BY 증명책임자부호