/* faa/cav/030/FAACAV030ESelectProfYmAblt */ SELECT NVL(PROF_YM,0) AS PROF_YM /* 증명연월 */
FROM TWFAACAV002H WHERE JRSD_CD||ACNTG_CD||BLL_KDCD||PROF_RBPRSN_SQNO||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO#||#ACNT_CD#||#BLL_CD#||#PROF_DRTR_SRNO#||#DL_XPN_RCDB_PBSVT_SRNO# AND PROF_YM > ( CASE WHEN #pType# = '01' THEN case WHEN SUBSTR(#ABLT_DT#,5,2)='12' THEN SUBSTR(#ABLT_DT#,1,4)||'13' ELSE #ABLT_DT# END ELSE CASE WHEN #pType# = '03' THEN CASE WHEN SUBSTR(#ABLT_DT#,5,2) > '09' THEN SUBSTR(#ABLT_DT#,1,4)||'04' ELSE CASE WHEN SUBSTR(#ABLT_DT#,5,2) > '06' THEN SUBSTR(#ABLT_DT#,1,4)||'03' ELSE CASE WHEN SUBSTR(#ABLT_DT#,5,2) > '03' THEN SUBSTR(#ABLT_DT#,1,4)||'02' ELSE SUBSTR(#ABLT_DT#,1,4)||'01' END END END END END ) AND BLL_RCPTN_STTS_SECD='A'