FROM TWFAACAV003M A, TBDCMACM015M B, TWFAACAV004L C WHERE B.INST_CLSF_TPCD = 'H' AND A.INST_CD = B.INST_CD AND A.JRSD_CD = C.JRSD_CD AND A.ACNTG_CD = C.ACNTG_CD AND A.BLL_KDCD = C.BLL_KDCD AND A.PROF_RBPRSN_SQNO = C.PROF_RBPRSN_SQNO AND A.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = C.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND C.PROF_YM LIKE #LAST_RCPT_YMD#||'%' ) AS 기타단체책수 FROM DUAL