/* faa/cav/044/FAACAV044ER2_1select */ SELECT B.ACNTG_CDNM AS 소관명 AS ACNTG_CDNM /* 회계코드명 */,
    C.ACNTG_CDNM AS 회계명 AS ACNTG_CDNM /* 회계코드명 */
FROM TWFAACAV061L A, TWFAACAV024C B, TWFAACAV024C C WHERE A.ACNTG_YR = #ACNTG_YR# AND A.SOA_SECD = 'A' AND A.SUM_SE_ID = '49' AND A.JRSD_CD = B.ACNTG_CD AND B.ACNTG_SECD = '02' AND A.ACNTG_CD = C.ACNTG_CD AND C.ACNTG_SECD = '01' GROUP BY A.JRSD_CD, A.ACNTG_CD, B.ACNTG_CDNM, C.ACNTG_CDNM