/* faa/cav/044/FAACAV044ER3select */ SELECT D1.PROF_RBPRSN_INST_NM AS 증명기관명 AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */,
    D5.JRSD_CD||'-'||D5.ACNTG_CD||'-'||D5.BLL_KDCD||'-'||D5.PROF_RBPRSN_SQNO||'-'||D5.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    D4.NTTR_ACTNO_ACTNO_SCRTY||'-'||D4.ACNT_SBJCT_SECD AS 계좌번호 AS NTTR_ACTNO_ACTNO_SCRTY /* 국고계좌번호계좌번호보안 */,
    NVL(D5.#N/A,0) AS 차감누계액 AS #N/A /* 전체결산금액2 */,
    NVL(D6.#N/A,0) AS 월계금액 AS #N/A /* 대사금액2 */,
    NVL(D5.#N/A,0)-NVL(D6.#N/A,0) AS 차액 AS #N/A /* 전체결산금액2 */
FROM TWFAACAV003M D1, TWFAACAV024C D2, TWFAACAV024C D3, TWFAACAV062L D4 , TWFAACAV061L D5, TWFAACAV010L D6 WHERE D5.ACNT_YR = #strYr# AND D5.SOA_SECD = 'A' AND D5.JRSD_CD IN ( SELECT DISTINCT NVL(D1.JRSD_CD,'') AS GPCD_JRSD_SECD /* 정부구매카드소관구분코드 */
FROM TWFAACAV061L D1, TWFAACAV024C D2, TWFAACAV024C D3 WHERE D1.ACNTG_YR = #ACNTG_YR# AND D1.SOA_SECD = 'B' AND D1.SUM_SE_ID = '49' AND D1.JRSD_CD = D2.ACNTG_CD AND D2.ACNTG_SECD = '02' AND D1.ACNTG_CD = D3.ACNTG_CD AND D3.ACNTG_SECD = '01' GROUP BY D1.JRSD_CD, D1.ACNTG_CD ) AND D5.ACNTG_CD IN( SELECT DISTINCT NVL(D1.ACNTG_CD,'') AS ACNTG_CD /* 회계코드 */
FROM TWFAACAV061L D1, TWFAACAV024C D2, TWFAACAV024C D3 WHERE D1.ACNTG_YR = #ACNTG_YR# AND D1.SOA_SECD = 'A' AND D1.SUM_SE_ID = '49' AND D1.JRSD_CD = D2.ACNTG_CD AND D2.ACNTG_SECD = '02' AND D1.ACNTG_CD = D3.ACNTG_CD AND D3.ACNTG_SECD = '01' GROUP BY D1.JRSD_CD, D1.ACNTG_CD ) AND D5.SUM_SE_ID = '49' AND D5.ACNTG_YR = D4.ACNTG_YR AND D5.JRSD_CD = D4.JRSD_CD AND D5.ACNTG_CD = D4.ACNTG_CD AND D5.BLL_KDCD = D4.BLL_KDCD AND D5.PROF_RBPRSN_SQNO = D4.PROF_RBPRSN_SQNO AND D5.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = D4.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND D5.JRSD_CD = D3.ACNTG_CD AND D3.ACNTG_SECD = '02' AND D5.ACNTG_CD = D2.ACNTG_CD AND D2.ACNTG_SECD = '01' AND D5.JRSD_CD = D1.JRSD_CD AND D5.ACNTG_CD = D1.ACNTG_CD AND D5.BLL_KDCD = D1.BLL_KDCD AND D5.PROF_RBPRSN_SQNO = D1.PROF_RBPRSN_SQNO AND D5.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = D1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND D6.BOK_ACTNO_SCRTY = D4.NTTR_ACTNO_ACTNO_SCRTY AND D6.ACNT_SBJCT_SECD = D4.ACNT_SBJCT_SECD AND D6.MNSUM_SECD = '01' AND SUBSTRING(D6.MNSUM_YM,5,2) = '13'