/* faa/cav/044/FAACAV044ERselect */ SELECT B.ACNTG_CD AS 소관부호 AS ACNTG_CD /* 회계코드 */,
    B.ACNTG_CDNM AS 소관명 AS ACNTG_CDNM /* 회계코드명 */,
    (CASE SUBSTRING(A.CHIF_CD,1,1) WHEN '2' THEN '방 위 비' WHEN '1' THEN '일반행정' WHEN '3' THEN '사회개발' WHEN '4' THEN '경제개발' WHEN '5' THEN '지방재정교부금' WHEN '6' THEN '채무상환기타' ELSE '' END) AS 기능 AS CHIF_CD /* 장코드 */,
    SUM(A.#N/A) AS 차감누계액 AS #N/A /* 전체결산금액2 */
FROM TWFAACAV061L A, TWFAACAV024C B WHERE A.ACNT_YR=#strYr# AND A.SOA_SECD='A' AND A.ACNTG_CD='011' AND A.SUM_SE_ID NOT IN ('49', '51') AND SUBSTRING(A.CHIF_CD,1,1) <> '0' AND A.JRSD_CD = B.ACNTG_CD AND B.ACNTG_SECD= '02' GROUP BY B.ACNTG_CD, B.ACNTG_CDNM, SUBSTRING(A.CHIF_CD,1,1)