UPDATE TWFAACAV021L SET BGT_ALC_AGGAMT = NVL(BGT_ALC_AGGAMT,0) + #BGT_ALC_AMT# , FND_ALC_AGGAMT = NVL(FND_ALC_AGGAMT,0) + #FND_ALC_AMT# , BEF_EXPN_AGG_AMT = NVL(BEF_EXPN_AGG_AMT,0) + REPLACE(#EXPN_AMT#,',','') , EXPN_SBTR_AGGAMT = NVL(EXPN_SBTR_AGGAMT,0) + (REPLACE(#RCTF_AMT#,',','') - (REPLACE(#EXTIN_AMT#,',','') - REPLACE(#EXPN_AMT#,',',''))) , FNDS_ALTMNT_BLNC = FNDS_ALTMNT_BLNC + (REPLACE(#EXTIN_AMT#,',','') - (REPLACE(#EXPN_AMT#,',','') - REPLACE(#FND_ALC_AMT#,',','') + REPLACE(#RCTF_AMT#,',',''))) /* 자금배정잔액 */WHERE JRSD_CD = SUBSTR(#FULL_PROF_DRTR_SRNO#,1,3) AND ACNTG_CD = SUBSTR(#FULL_PROF_DRTR_SRNO#,5,3) AND BLL_KDCD = SUBSTR(#FULL_PROF_DRTR_SRNO#,9,2) AND PROF_RBPRSN_SQNO = SUBSTR(#FULL_PROF_DRTR_SRNO#,12,3) AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = SUBSTR(#FULL_PROF_DRTR_SRNO#,16,3) AND PROF_YM > REPLACE(#PROF_YM#,'-','') AND SSEC_CD = #SSEC_CD# AND ITM_CD = #ITM_CD# AND DITM_CD = #DITM_CD# AND SITM_CD = #SITM_CD#