UPDATE TWFAACAV021L SET BGT_ALC_AGGAMT = BGT_ALC_AGGAMT - REPLACE(#BGT_ALC_AMT#,',','') ,  FND_ALC_AGGAMT = FND_ALC_AGGAMT - REPLACE(#FND_ALC_AMT#,',','') ,  EXPN_SBTR_AGGAMT = NVL(EXPN_SBTR_AGGAMT,0) - (REPLACE(#RCTF_AMT#,',','') - (REPLACE(#EXTIN_AMT#,',','') - REPLACE(#EXPN_AMT#,',',''))) ,  FNDS_ALTMNT_BLNC = FNDS_ALTMNT_BLNC - (REPLACE(#EXTIN_AMT#,',','') - (REPLACE(#EXPN_AMT#,',','') - REPLACE(#FND_ALC_AMT#,',','') + REPLACE(#RCTF_AMT#,',',''))) /* 자금배정잔액 */WHERE JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# AND (PROF_YM != #PROF_YM# OR TPOF_CYCL > #DGE#) AND SSEC_CD = #SSEC_CD# AND ITM_CD = #ITM_CD# AND DITM_CD = #DITM_CD# AND SITM_CD = #SITM_CD# AND SUBSTR(PROF_YM,1,4) IN (SUBSTR(#PROF_YM#,1,4)) AND PROF_YM IN (#PROF_YM#, #PROF_YM# + 1, #PROF_YM# + 2) AND PROF_YM IN (#PROF_YM#, #PROF_YM# + 1) AND PROF_YM = #PROF_YM# AND SUM_SE_ID != '49'