/* faa/cav/061/FAACAV061R_02 */ SELECT JRSD_CD||'-'||ACNTG_CD||'-'||BLL_KDCD||'-'||PROF_RBPRSN_SQNO||'-'||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    SUBSTR(PROF_YM,1,4) || '년 ' || SUBSTR(PROF_YM,5,2) || '월' AS 분기 AS PROF_YM /* 증명연월 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    SUBSTRB(ASST_DATA_ATCH_CN, 1, 60) AS SUBSTRB,
    TO_NUMBER(SUBSTRB(ASST_DATA_ATCH_CN, 61, 15)) AS TO_NUMBER,
    TO_NUMBER(SUBSTRB(ASST_DATA_ATCH_CN, 77, 15)) AS TO_NUMBER,
    SUBSTRB(ASST_DATA_ATCH_CN, 98, 300) AS RMRK_CN /* 비고내용 */
FROM TWFAACAV009L WHERE ASST_DATA_ATCH_SECD = 'ATC280202' AND SUM_SE_ID != '49' AND PROF_YM = #PROF_YM#||#strMm# AND JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# ORDER BY 증명책임자부호, SUM_SE_ID DESC