    TO_NUMBER(SUBSTRB(ASST_DATA_ATCH_CN, 159, 15)) AS TO_NUMBER,
    TO_NUMBER(SUBSTRB(ASST_DATA_ATCH_CN, 175, 15)) AS TO_NUMBER,
