/* faa/cav/067/FAACAV067EselectReport3 */ SELECT JRSD_CD||'-'||ACNTG_CD||'-'||BLL_KDCD||'-'||PROF_RBPRSN_SQNO||'-'||GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AS 증명책임자부호 AS JRSD_CD /* 소관코드 */,
    F_AV_ACNT_CD_NM(ACNTG_CD) AS 회계명 AS ACNTG_CD /* 회계코드 */,
    F_AV_BLL_COPT_CD_NM(JRSD_CD) AS 소관명 AS JRSD_CD /* 소관코드 */,
    ASST_DATA_ATCH_SQNO AS 일련번호 AS ASST_DATA_ATCH_SQNO /* 보조자료첨부순번 */,
    SUBSTRB(ASST_DATA_ATCH_CN,1,61) AS 적요 AS ASST_DATA_ATCH_CN /* 보조자료첨부내용 */,
    TO_NUMBER(SUBSTRB(ASST_DATA_ATCH_CN,62,15)) AS 현금영수액 AS ASST_DATA_ATCH_CN /* 보조자료첨부내용 */,
    TRIM(SUBSTRB(ASST_DATA_ATCH_CN,77,200)) AS 비고 AS ASST_DATA_ATCH_CN /* 보조자료첨부내용 */
FROM TWFAACAV009L WHERE ASST_DATA_ATCH_SECD = 'ATC270203' AND SUM_SE_ID != '49' AND PROF_YM = #PROF_YM#||#strMm# AND JRSD_CD = #JRSD_CD# AND ACNTG_CD = #ACNTG_CD# AND BLL_KDCD = #BLL_KDCD# AND PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# ORDER BY 증명책임자부호, ASST_DATA_ATCH_SQNO