SELECT APL_NAM,
    COUNT(PMT_AM) AS PMT_AM,
    NVL(SUM(PMT_AM),0) AS PMT_AM,
    NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBFAACAV074M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH ='01' AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) GROUP BY APL_NAM ),0) AS PMT_SUM_01 /* 온라인 건 표시 */ ,NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBFAACAV074M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH ='02' AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) GROUP BY APL_NAM ),0) AS PMT_SUM_02 /* 오프라인건건 표시 */ ,NVL((SELECT SUM(PMT_AM) AS PMT_AM
FROM TBFAACAV074M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH ='03' AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) /* 승인된것만 표시 */ GROUP BY APL_NAM ),0) AS PMT_SUM_03 /* 도서분야건 표시 */ FROM TBFAACAV074M WHERE APL_CNB = #strUserCnb# AND LINK_YR = TO_CHAR(SYSDATE,'YYYY') AND SPH IN ('01','02','03') AND (HNDL_STEP='3' OR HNDL_STEP='6' OR HNDL_STEP='7' ) /* 승인된것만 표시 */ GROUP BY APL_NAM