/* faa/cav/076/FAACAV076ERcptHstSelect */ SELECT D1.PROF_YM /* 증명연월 */,
    D1.JRSD_CD /* 소관코드 */,
    D1.ACNTG_CD /* 회계코드 */,
    D1.BLL_KDCD /* 계산서종류코드 */,
    D1.PROF_RBPRSN_SQNO /* 증명책임자순번 */,
    D1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO /* 관서운영경비출납공무원순번 */,
    TO_CHAR(D1.RCPTN_DT, 'YYYY.MM.DD HH24:MI:SS') AS RCPTN_DT /* 수신일시 */,
    D1.BLL_RCPTN_STTS_SECD /* 계산서수신상태구분코드 */,
    (SELECT AS CD_NM
FROM TBDCMACM013C WHERE CD = D1.BLL_RCPTN_STTS_SECD AND CMM_CD_DVSN_CD = '1000701') AS RCPT_STA_CD_NM , DECODE(D1.BLL_RCPTN_STTS_SECD, 'E', ( SELECT AGGR_CONCAT( S1.BLL_RCPT_ERR_SECD, ',') AS BLL_RCPT_ERR_SECD /* 계산서접수오류구분코드 */
FROM TWFAACAV006D S1 WHERE S1.PROF_YM = D1.PROF_YM AND S1.JRSD_CD = D1.JRSD_CD AND S1.ACNTG_CD = D1.ACNTG_CD AND S1.BLL_KDCD = D1.BLL_KDCD AND S1.PROF_RBPRSN_SQNO = D1.PROF_RBPRSN_SQNO AND S1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = D1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO AND S1.CRU_RCPT_DT = D1.BLL_RCPTN_DT GROUP BY S1.PROF_YM , S1.JRSD_CD , S1.ACNTG_CD , S1.BLL_KDCD , S1.PROF_RBPRSN_SQNO , S1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO , S1.CRU_RCPT_DT )) AS BLL_RCPT_ERR_SECD FROM TWFAACAV002H D1 WHERE D1.PROF_YM = #PROF_YM# AND D1.JRSD_CD = #JRSD_CD# AND D1.ACNTG_CD = #ACNTG_CD# AND D1.BLL_KDCD = #BLL_KDCD# AND D1.PROF_RBPRSN_SQNO = #PROF_RBPRSN_SQNO# AND D1.GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO = #GVMNFC_OPER_EPS_RCDB_PBOFC_SQNO# ORDER BY RCPTN_DT