UPDATE TBHRMCEX004M T1 SET ATRZ_DOC_NO = #ATRZ_DOC_NO# /* 결재문서번호 */, ATRZ_STTS_SECD = #ATRZ_STTS_SECD# /* 결재상태구분코드 */, PRGRS_STTS_SECD = CASE WHEN #ATRZ_STTS_SECD# = '3' THEN '102' WHEN #ATRZ_STTS_SECD# = '2' THEN '107' ELSE PRGRS_STTS_SECD END /* 진행상태구분코드 */, SBMSN_YMD = CASE WHEN #ATRZ_STTS_SECD# = '3' THEN TO_CHAR(SYSDATE,'YYYYMMDD') ELSE SBMSN_YMD END /* 제출일자 */WHERE 1=1 AND T1.EXPRT_SRNO = #EXPRT_SRNO# AND T1.RPRT_SRNO = #RPRT_SRNO# AND ATRZ_DOC_NO = #ATRZ_DOC_NO#