MERGE INTO PTL_BAI_NEWS A USING ( SELECT #NTT_ID# AS #NTT_ID#,
    #BBS_ID# AS BBS_ID /* 게시판아이디 */
FROM DUAL ) B ON ( A.NTT_ID = B.NTT_ID AND A.BBS_ID = B.BBS_ID ) WHEN MATCHED THEN UPDATE SET NTT_NO = #NTT_NO# , NTT_SJ = #NTT_SJ# , NTT_CN = #NTT_CN# , NTCR_NM = #NTCR_NM# , FRST_REGIST_PNTTM = #FRST_REGIST_PNTTM# , MAIN_IMG_ID = #MAIN_IMG_ID# , THUMB_IMG_ID = #THUMB_IMG_ID# , LIST_IMG_ID = #LIST_IMG_ID# WHEN NOT MATCHED THEN INSERT INSERT ( NTT_ID ,BBS_ID ,NTT_NO ,NTT_SJ ,NTT_CN ,NTCR_NM ,FRST_REGIST_PNTTM ,MAIN_IMG_ID ,THUMB_IMG_ID ,LIST_IMG_ID ) VALUES ( #NTT_ID# ,#BBS_ID# ,#NTT_NO# ,#NTT_SJ# ,#NTT_CN# ,#NTCR_NM# ,#FRST_REGIST_PNTTM# ,#MAIN_IMG_ID# ,#THUMB_IMG_ID# ,#LIST_IMG_ID# )