SELECT T1.EVL_YR /* 평가년도 */,
    T1.HLYR_DVSN_CD /* 반기구분코드 */,
    T1.EVL_DGE /* 평가차수 */,
    T1.EVL_TG_DPT_CD /* 평가요청부서코드 */,
    (SELECT AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBRESRCM005M S1 WHERE S1.EVL_YR = T1.EVL_YR AND S1.HLYR_DVSN_CD = T1.HLYR_DVSN_CD AND S1.EVL_DGE = T1.EVL_DGE AND S1.SRECS_DEPT_CD = T1.EVL_TG_DPT_CD ) AS EVL_TG_DPT_NM ,T1.EVL_TIT /* 평가제목 */ ,T1.SPE_ADP_SNO /* 특별가산점순번 */ ,T1.CLU_CD /* 특별가산점 항목 코드 */ ,(SELECT CD_NM
FROM TBRESRCM006C S1 WHERE S1.EVL_YR = T1.EVL_YR AND S1.HLYR_DVSN_CD = T1.HLYR_DVSN_CD AND S1.EVL_DGE = T1.EVL_DGE AND S1.CD_DVSN_CD = 'R000' AND S1.CD = T1.CLU_CD ) AS CLU_NM FROM TBRESRAD016M T1 WHERE 1=1 AND T1.EVL_YR = #EVL_YR# AND T1.HLYR_DVSN_CD = #HLYR_DVSN_CD# AND T1.EVL_DGE = #EVL_DGE# AND T1.SPE_ADP_SNO IN #SPE_ADP_SNO[]# AND T1.ATRZ_DOC_NO = #ATRZ_DOC_NO# ORDER BY T1.EVL_YR ,T1.HLYR_DVSN_CD ,T1.EVL_DGE ,T1.SPE_ADP_SNO