SELECT T1.EVL_YR,
    T1.HLYR_DVSN_CD,
    T1.EVL_DGE,
    T1.CVLCPT_ADT_YR /* 민원감사년도 */,
    T1.ADT_NO /* 감사번호 */,
    T1.ADT_KDCD /* 감사종류코드 */,
    CASE WHEN T1.AT_REGI_YN ='N' THEN T1.CVLCPT_ADT_YR||'-'||T1.ADT_NO ELSE F_MNG_KND_AUDYRNO(T1.CVLCPT_ADT_YR, T1.ADT_NO, null, '-') END AS ADT_CLSF_NO /* 감사분류번호 */,
    ADT_MTTR_NM /* 감사사항명 */,
    (SELECT S1.CD_NM
FROM TBRESRCM006C S1 WHERE S1.EVL_YR = T1.EVL_YR AND S1.HLYR_DVSN_CD = T1.HLYR_DVSN_CD AND S1.EVL_DGE = T1.EVL_DGE AND S1.CD_DVSN_CD = 'F000' AND S1.CD <> '0000' AND S1.CD = T1.ADT_KDCD ) AS AUD_KND_NM ,(SELECT CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBRESRCM005M S1 WHERE S1.EVL_YR = T1.EVL_YR AND S1.HLYR_DVSN_CD = T1.HLYR_DVSN_CD AND S1.EVL_DGE = T1.EVL_DGE AND S1.SRECS_DEPT_CD = T1.SPRVSN_DEPT_CD ) SPV_BRDV_NM ,TO_CHAR(TO_DATE(T1.ENFC_YMD),'YYYY-MM-DD') AS ENFC_YMD FROM TBRESRAD010M T1 WHERE 1=1 AND T1.EVL_YR = #EVL_YR# AND T1.HLYR_DVSN_CD = #HLYR_DVSN_CD# AND T1.EVL_DGE = #EVL_DGE# AND T1.CVLCPT_ADT_YR||T1.ADT_NO IN #AUD_YR_NO[]# AND T1.ATRZ_DOC_NO = #ATRZ_DOC_NO# ORDER BY T1.EVL_YR ,T1.HLYR_DVSN_CD ,T1.EVL_DGE ,T1.CVLCPT_ADT_YR, T1.ADT_NO